import axios from "axios";
import { useState } from "react";
import "./css/otp.css";

const Otp = () => {
    const [email,setEmail] = useState("")
    const [otp,setotp] = useState({
        otp1 : "",
        otp2 : "",
        otp3: "",
        otp4 : ""    
    })

    const inputChange = (event) => {
        setEmail(event.target.value)
        const name = event.target.name
        const value = event.target.value
        setotp((prevalue) => {
            return{
                ...prevalue,[name]:value
            }
        })
    }

    const sendOtp = async() => {
        await axios({
            method : 'post',
            url : 'http://127.0.0.1:8000/auth/verify/',
            headers : {
                'Content-Type' : 'application/json'
            },
            data : {
                'email' : email,
                'otp' : otp.otp1 + otp.otp2 + otp.otp3 + otp.otp4
            }
        })
    }
    
    return (
        <>
            <div className="container">
                Enter the code generated on your email below to be active user !



                <form className="digit-group" data-group-name="digits" data-autosubmit="true" autocomplete="on">

                    <div className="box">
                        <label for="email" className="fl fontLabel"> Email ID: </label>
                        <div className="fl iconBox"><i className="fa fa-envelope" aria-hidden="true"></i></div>
                        <div className="fr">

                            <input type="email" required value={email} name="email" placeholder="Email Id" onChange={inputChange} className="textBox" />
                        </div>
                    </div>
                    <input type="text" id="digit-1" name="otp1" data-next="digit-2" onChange={otp.otp1}/>
                    <input type="text" id="digit-2" name="otp2" data-next="digit-3" onChange={otp.otp2}  />
                    <input type="text" id="digit-3" name="otp3" data-next="digit-4" onChange={otp.otp3}  />
                    <input type="text" id="digit-3" name="otp4" data-next="digit-4" onChange={otp.otp4}  />
                </form>
            </div>
        </>
    )
}

export default Otp;
