import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./css/otp.css";

const Otp = () => {
    let navigate = useNavigate();
    const [email, setEmail] = useState("")
    const [otp, setotp] = useState("")

    const inputChange = (event) => {
        setEmail(event.target.value)
    }

    const sendOtp = async () => {
        await axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/auth/verify/',
            headers: {
                'Content-Type': 'application/json'
            },
            data: {
                'email': email,
                'otp': otp
            }
        }).then((response) => {
            console.log(response.data)
            // console.log(response.status)
            if (response.data.status == 200) {
                navigate('/login')
            } else {
                navigate('/otp')
            }
        }).catch((error) => {
            console.error(error.response)
        })
    }

    const submitData = (event) => {
        event.preventDefault()
        sendOtp()
    }


    return (
        <>
            <div className="container">
                Enter the code generated on your email below to be active user !
                <div className="box">
                    <label for="email" className="fl fontLabel"> Email ID: </label>
                    <div className="fl iconBox"><i className="fa fa-envelope" aria-hidden="true"></i></div>
                    <div className="fr">

                        <input type="email" required value={email} name="email" placeholder="Email Id" onChange={inputChange} className="textBox" />
                    </div>
                </div>
                <br />
                <br />
                Code



                <form onSubmit={submitData} className="digit-group" data-group-name="digits" data-autosubmit="true" autocomplete="on">
                    <label for="email" className="fl fontLabel"> Email ID: </label>
                    <div className="fl iconBox"><i className="fa fa-envelope" aria-hidden="true"></i></div>
                    <div className="fr">

                        <input type="email" required value={email} name="email" placeholder="Email Id" onChange={inputChange} className="textBox" />
                    </div>

            <button className="submit" value="submit" >Submit</button>
        </form>
            </div >
        </>
    )
}

export default Otp;
