import { useState } from "react";
import "./css/otp.css";

const Otp = () => {
    const [otpDetails,setotpDetails] = useState(
        {
            email : "",
            otp : {
                otp1 : "",
                otp2 : "",
                otp3 : "",
                otp4 : ""
            }
        })
    const inputChange = (event) => {
        const name = event.target.name
        const value = event.target.value
        setotpDetails((prevalue) => {
            return{
                ...prevalue,[name]:value
            }
        })
    }
    
    return (
        <>
            <div className="container1">
                Enter the code generated on your email below to be active user !



                <form className="digit-group" data-group-name="digits" data-autosubmit="true" autocomplete="on">

                    <div className="box">
                        <label for="email" className="fl fontLabel"> Email ID: </label>
                        <div className="fl iconBox"><i className="fa fa-envelope" aria-hidden="true"></i></div>
                        <div className="fr">

                            <input type="email" required value={otpDetails.email} name="email" placeholder="Email Id" onChange={inputChange} className="textBox" />
                        </div>
                    </div>
                    <input type="text" id="digit-1" name="otp1" data-next="digit-2" onChange={otpDetails.otp.otp1}/>
                    <input type="text" id="digit-2" name="otp2" data-next="digit-3" onChange={otpDetails.otp.otp2}  />
                    <input type="text" id="digit-3" name="otp3" data-next="digit-4" onChange={otpDetails.otp.otp3}  />
                    <input type="text" id="digit-3" name="otp4" data-next="digit-4" onChange={otpDetails.otp.otp4}  />
                </form>
            </div>
        </>
    )
}

export default Otp;
