import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./css/otp.css";

const Otp = () => {
    let navigate = useNavigate();
    const [otpDetails, setotpDetails] = useState({
        email: "",
        otp: ""
    })
    const inputChange = (event) => {

    }

    const sendOtp = async () => {
        await axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/auth/verify/',
            headers: {
                'Content-Type': 'application/json'
            },
            data: {
                'email': otpDetails.email,
                'otp': otpDetails.otp
            }
        }).then((response) => {
            console.log(response.data)
            // console.log(response.status)
            if (response.data.status == 200) {
                navigate('/login')
            } else {
                navigate('/otp')
            }
        }).catch((error) => {
            console.error(error.response)
        })
    }

    const submitData = (event) => {
        event.preventDefault()
        sendOtp()
    }


    return (
        <>
            <div className="card" style={{"width": "18rem"}}>
                <div className="card-body">
                    <h5 className="card-title">OTP</h5>
                    <h6 className="card-subtitle mb-2 text-muted">Enter OTP sent in your email</h6>
                    <input type="email" required value={otpDetails.email} name="email" maxlength="15" onChange={inputChange} placeholder="Email." className="textBox" />
                    <a href="#" className="card-link">Card link</a>
                    <a href="#" className="card-link">Another link</a>
                </div>
            </div>
        </>
    )
}

export default Otp;
